mapper.select:
select EMAIL
  from USER
 where EMAIL=#{email}

select NICKNAME
  from USER
 where NICKNAME=#{nickname}

select NICKNAME
  from USER
 where EMAIL=#{email}

select PASSWORD
  from USER
 where EMAIL=#{email}


select EMAIL, PASSWORD, NICKNAME, PHONE_NUM
  from USER
 where EMAIL=#{email}

mapper.update:
update USER
   set NICKNAME=#{nickname}, PASSWORD=#{password}
 where EMAIL=#{email}

mapper.select:
select USER_ID
  from USER
 where EMAIL=#{user}
